package HomeWork2.loops;import java.util.Scanner;public class Task1_2 {    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        System.out.println("Vvedite celoe chislo");       if (in.hasNextInt()) {           int cifra= Integer.parseInt(in.next());           System.out.println(verniProiz(cifra));       } else if(in.hasNextDouble()){           System.out.println("Vvedeno ne celoe chislo");       } else {       System.out.println("Vvedeni nevernie dannie");       }    }    public static int verniProiz (int x){        int k=0;        int b=1;        int j=0;        int y=x;        while (y>0){            y/=10;            j++;        }        for (int i =0;i<j;i++){            k=x%10;            b*=k;            x/=10;        }        return b;    }}